sys_variable_value.value:
(function execute(inputs, outputs) {
   outputs.output_state = "error"; 
   if (inputs.record_id && inputs.flow_context_id ) {
     var input_flow_id = inputs.flow_id;
   	 if (!inputs.flow_id) {
    	var gr_sys_flow_context = new GlideRecord("sys_flow_context");
        if (gr_sys_flow_context.get(inputs.flow_context_id)) {
        	input_flow_id = gr_sys_flow_context.flow+'';
        }
     }
     if (input_flow_id) {
        var gr_sys_flow_context = new GlideRecord("sys_flow_context");
        gr_sys_flow_context.addEncodedQuery("stateINWAITING,IN_PROGRESS,QUEUED");
        gr_sys_flow_context.addQuery("source_record", inputs.record_id);
        gr_sys_flow_context.addQuery("sys_id", "!=", inputs.flow_context_id);
        gr_sys_flow_context.addQuery("flow" , input_flow_id);
        gr_sys_flow_context.orderBy("sys_created_on");
        gr_sys_flow_context.query();
        while (gr_sys_flow_context.next()) {
          sn_fd.FlowAPI.cancel(gr_sys_flow_context.getUniqueValue(), "Auto Cancelled from a flow step");
            outputs.cancelled_flow_id = gr_sys_flow_context.getUniqueValue()+'';
            outputs.output_state = "success";  
        }
     }
   }

})(inputs, outputs);

